sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },
    isDatePast: function(strDate) {
        // Create GlideDateTime objects for the current date and the passed-in date
        var rightNow = new GlideDateTime();
        var submitDate = new GlideDateTime(strDate);

        // If the submitDate is before rightNow, return true, else return false
        if (submitDate.before(rightNow)) {
            return true;
        } else {
            return false;
        }
    },
    isDateEqualCurrent: function(strDate) {
        var today = new GlideDateTime();
        var isToday = new GlideDateTime(strDate);

        // Create GlideDateTime objects for the current date and the passed-in date
        if (today.compareTo(isToday) === 0) {
            return true;
        } else {
            return false;
        }
    },
    type: 'NeedItUtils'
};
